"use client";

import { Account, AuthenticatedUser } from "@lens-protocol/react";
import { Avatar, AvatarFallback, AvatarImage } from "@/registry/new-york/ui/avatar";
import { UserCircle2, ChevronRight, Loader, CheckCircle2Icon } from "lucide-react";
import { truncateAddress } from "@/registry/new-york/common/lib/lens-utils";
import { ReactNode } from "react";

type LensAccountListItemProps = {
  account: Account;
  selectedAccount?: Account | null;
  onAccountSelected?: (account: Account) => void;
  authenticatedUser?: AuthenticatedUser | null;
  showChevron?: boolean;
  renderDivider?: () => ReactNode;
};

export function LensAccountListItem({
  account,
  selectedAccount,
  onAccountSelected,
  authenticatedUser,
  showChevron = true,
  renderDivider,
}: LensAccountListItemProps) {
  const isAuthenticated = authenticatedUser?.address.toLowerCase() === account.address.toLowerCase();
  const isSelected = onAccountSelected && isAuthenticated;

  const Divider = renderDivider;

  return (
    <div>
      <button
        type="button"
        disabled={selectedAccount?.address !== undefined || isSelected}
        onClick={() => onAccountSelected?.(account)}
        className={`w-full flex items-center text-start p-2 gap-2 enabled:cursor-pointer enabled:hover:bg-neutral-100 enabled:focus:bg-neutral-100 focus:outline-0 rounded-sm ${isSelected ? "border" : "disabled:opacity-45"}`}
      >
        <Avatar className="flex-none w-10 h-10">
          <AvatarImage src={account.metadata?.picture} alt="Account avatar" />
          <AvatarFallback>
            <UserCircle2 className="w-10 h-10 opacity-45" />
          </AvatarFallback>
        </Avatar>
        <div className="flex-grow flex flex-col w-full min-w-0">
          <div className="flex gap-1 items-center">
            <span className="text-sm font-semibold">
              {account.metadata?.name ?? account.username?.localName ?? "[anonymous]"}
            </span>
          </div>
          <span className="text-xs opacity-65 truncate">
            {account.metadata?.name ? "@" + account.username?.localName : truncateAddress(account.address)}
          </span>
        </div>
        {selectedAccount?.address == account.address ? (
          <Loader className="animate-spin flex-none opacity-45 w-4 h-4" />
        ) : showChevron && isSelected ? (
          <CheckCircle2Icon className="flex-none opacity-45 w-4 h-4 text-accent-foreground" />
        ) : (
          showChevron && <ChevronRight className="flex-none opacity-45 w-4 h-4" />
        )}
      </button>
      {Divider && <Divider />}
    </div>
  );
}

import { Skeleton } from "@/registry/new-york/ui/skeleton";

export const LensAccountListItemSkeleton = () => {
  return (
    <div className="flex items-center space-x-2 p-2">
      <Skeleton className="h-10 w-10 rounded-full flex-none" />
      <div className="flex-grow space-y-1.5">
        <Skeleton className="h-4 w-5/12" />
        <Skeleton className="h-3 w-3/12" />
      </div>
      <Skeleton className="w-4 h-4" />
    </div>
  );
};

import { Cursor, Paginated } from "@lens-protocol/react";
import { ReactNode, useCallback, useEffect, useState } from "react";
import { AlertTriangle, Loader } from "lucide-react";
import { Virtuoso } from "react-virtuoso";

type PaginationListProps<Type> = {
  className?: string;
  data: Paginated<Type> | undefined;
  loading: boolean;
  error: Error | undefined;
  cursor?: Cursor | null;
  setCursor: (cursor: Cursor | null) => void;
  renderItem: (item: Type, index: number) => ReactNode;
  renderHeader?: () => ReactNode;
  renderFooter?: () => ReactNode;
  renderSkeleton?: () => ReactNode;
  emptyMessage?: string;
  errorMessage?: string;
  filterFn?: (item: Type) => boolean;
  sortFn?: (a: Type, b: Type) => number;
};

const PaginatedList = <Type extends any>(props: PaginationListProps<Type>) => {
  const [items, setItems] = useState<Type[]>([]);

  const DefaultHeader = (): ReactNode => {
    return <div className="h-2"></div>;
  };

  const DefaultFooter = (): ReactNode => {
    if (!loading || !items) return <div className="h-2"></div>;
    return (
      <div className="w-full h-16 flex items-center justify-center">
        <Loader className="animate-spin flex-none opacity-45 w-6 h-6" />
      </div>
    );
  };

  const {
    className,
    data,
    loading,
    error,
    filterFn,
    cursor,
    setCursor,
    renderItem,
    renderSkeleton,
    renderHeader = DefaultHeader,
    renderFooter = DefaultFooter,
    emptyMessage,
    errorMessage,
    sortFn,
  } = props;

  useEffect(() => {
    if (data?.items) {
      const items = filterFn ? data.items.filter(filterFn) : data.items;
      const sortedItems = sortFn ? items.toSorted(sortFn) : items;
      setItems(prevItems => {
        return cursor ? [...prevItems, ...sortedItems] : [...sortedItems];
      });
    }
  }, [data]);

  const loadMore = useCallback(() => {
    if (!data || !data.pageInfo.next) return;
    setCursor(data.pageInfo.next as Cursor);
  }, [data, setCursor]);

  const Skeleton = (): ReactNode => {
    return renderSkeleton ? (
      renderSkeleton()
    ) : (
      <div className="w-full h-full flex items-center justify-center">
        <Loader className="animate-spin flex-none opacity-45 w-6 h-6" />
      </div>
    );
  };

  const Header = renderHeader;
  const Footer = renderFooter;

  return (
    <div className="h-full">
      {error ? (
        <div className="py-4 flex gap-4 items-center">
          <AlertTriangle className="w-8 h-8" />
          {errorMessage ?? "Error loading data"}
        </div>
      ) : !items.length && loading ? (
        <div className={"h-full" + (className ? ` ${className}` : "")}>
          <Header />
          <Skeleton />
          <Footer />
        </div>
      ) : (
        <div className={"h-full" + (className ? ` ${className}` : "")}>
          {items.length === 0 && (
            <div className="py-4 h-full flex items-center justify-center text-sm">
              {emptyMessage ?? "Nothing to show"}
            </div>
          )}
          <Virtuoso
            className="h-full"
            data={items}
            endReached={loadMore}
            itemContent={(index, item) => renderItem(item, index)}
            components={{ Header, Footer }}
          />
        </div>
      )}
    </div>
  );
};

export default PaginatedList;

import { clsx, type ClassValue } from "clsx";
import { twMerge } from "tailwind-merge";

export const ZeroAddress = "0x0000000000000000000000000000000000000000";
export const NATIVE_TOKEN = "0x000000000000000000000000000000000000800A";

export function cn(...inputs: ClassValue[]) {
  return twMerge(clsx(inputs));
}

/**
 * Truncate a string to a maximum length, adding an ellipsis in the middle
 * @param address The string to truncate
 * @param maxLength The maximum length of the truncated string, excluding the ellipsis and 0x prefix
 */
export const truncateAddress = (address: string, maxLength: number = 8): string => {
  if (address.length <= maxLength) {
    return address;
  }
  const ellipsis = "â€¦";
  const startLength = Math.ceil((maxLength + ellipsis.length) / 2) + 1;
  const endLength = Math.floor((maxLength + ellipsis.length) / 2);
  return address.slice(0, startLength) + ellipsis + address.slice(address.length - endLength);
};

export const getCidFromIpfsUrl = (ipfsUrl: string): string => {
  if (!ipfsUrl.startsWith("ipfs://")) throw new Error("IPFS urls must begin with ipfs://");
  return ipfsUrl.replace("ipfs://", "").replace(/^\/+|\/+$/g, "");
};

export const ipfsUrlToGatewayUrl = (
  ipfsUrl: string | undefined,
  gatewayDomain: string = "https://ipfs.io/ipfs/",
): string | undefined => {
  if (!ipfsUrl || ipfsUrl.length === 0 || !ipfsUrl.startsWith("ipfs://")) return ipfsUrl;
  const cid = getCidFromIpfsUrl(ipfsUrl);
  const gatewayUrl = gatewayDomain + cid;
  const path = ipfsUrl.split(cid)[1];
  return path ? `${gatewayUrl}${path}` : gatewayUrl;
};

export const arweaveUrlToGatewayUrl = (
  arUrl: string | undefined,
  gatewayDomain: string = "https://arweave.net/",
): string | undefined => {
  if (!arUrl || arUrl.length === 0 || !arUrl.startsWith("ar://")) return arUrl;
  const txId = arUrl.replace("ar://", "");
  return `${gatewayDomain}${txId}`;
};

export const lensUrlToGatewayUrl = (
  lensUrl: string | undefined,
  gatewayDomain: string = "https://api.grove.storage/",
): string | undefined => {
  if (!lensUrl || lensUrl.length === 0 || !lensUrl.startsWith("lens://")) return lensUrl;
  const txId = lensUrl.replace("lens://", "");
  return `${gatewayDomain}${txId}`;
};

/**
 * Parse a URI and convert it to a gateway URL if it is an IPFS, Arweave, or Lens URL
 * @param uri The URI to parse
 */
export const parseUri = (uri: string | null | undefined): string | undefined => {
  if (!uri) return undefined;

  try {
    const { protocol } = new URL(uri);
    switch (protocol) {
      case "ipfs:":
        return ipfsUrlToGatewayUrl(uri);
      case "ar:":
        return arweaveUrlToGatewayUrl(uri);
      case "lens:":
        return lensUrlToGatewayUrl(uri);
      default:
        return uri;
    }
  } catch {
    return undefined;
  }
};
